import React, {Component } from 'react';

import './Setter.css'

export default class Setter extends Component {
    constructor(props) {
        super(props);
        this.state = {
            breakCount: 5,
            sessionCount: 25
        }
    }
        bIncrement() {
            this.setState({
              breakCount: this.state.breakCount + 1
            });
          };

        bDecrement() {
            this.setState({
                breakCount: this.state.breakCount - 1
            });
          };


        sIncrement() {
            this.setState({
                sessionCount: this.state.sessionCount + 1
            });
        };

        sDecrement() {
            this.setState({
                sessionCount: this.state.sessionCount - 1
            });
        };


    render() {
        return (
            <div className="setter_div">
                <div className="break_div set_length">
                    <p>Break Length</p>
                    <div>
                        <div className="set">
                            <button id="down" onClick={(e) => this.bDecrement(e)}></button> 
                        </div>
                        <div className="set number">{this.state.breakCount}</div>
                        <div className="set">
                            <button id="up" onClick={(e) => this.bIncrement(e)}></button> 
                        </div>
                    </div>
                </div>

                <div className="session_div set_length">
                    <p>Session Length</p>
                    <div>
                        <div className="set">
                            <button id="down" onClick={(e) => this.sDecrement(e)}></button> 
                        </div>
                        <div className="set number">{this.state.sessionCount}</div>
                        <div className="set">
                            <button id="up" onClick={(e) => this.sIncrement(e)}></button> 
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}
