import React, {Component } from 'react';
import './App.css';
import Setter from './components/Setter/Setter';
import Timer from './components/Timer/Timer';
import Controls from './components/Controls/Controls';
import Sound from './components/Sound/Sound';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        breakCount: 5,
        sessionCount: 25,
        cycle: 'Session',
        sound: true,
    }
}

    switchMode = () => {
      if (this.state.cycle === 'Session') {
        this.setState({
          cycle: 'Break'
        })
      }
      else {
        this.setState({
          cycle: 'Session'
        })
      }
    }

    toggleSound = () => {
      this.setState({
        sound: !this.state.sound
      })
    }

    //I think this will play the sound
    // setSound = (sound) => {
    //   this.setState({
    //     sound: sound
    //   })
    // };

    bIncrement = ()  => {
        if (this.state.breakCount < 60) {
            this.setState({
                breakCount: this.state.breakCount + 1
              });
        }

      };

    bDecrement = () => {
        if (this.state.breakCount > 0) {
            this.setState({
                breakCount: this.state.breakCount - 1
            });
        }
      };


    sIncrement = () => {
        if (this.state.sessionCount < 60) {
            this.setState({
                sessionCount: this.state.sessionCount + 1
            });
        } 
    };

    sDecrement = () => {
        if (this.state.sessionCount > 0) {
            this.setState({
                sessionCount: this.state.sessionCount - 1
            });
        }
    };

    // countDown = () => {
    //   // if (this.state.sessionCount !== 0) {
    //   //   startTime = new Date().getTime();
    //   //   tInterval = setInterval(getShowTime, 1);
    //   // }
    // }
  render() {
    return (
      <div className="container_div">
        <h1>Pomodoro Clock</h1>
        <Setter 
          breakCount={this.state.breakCount}
          sessionCount={this.state.sessionCount}
          bIncrement={this.bIncrement}
          bDecrement={this.bDecrement}
          sIncrement={this.sIncrement}
          sDecrement={this.sDecrement}
        />
        <Timer 
          breakCount={this.state.breakCount}
          sessionCount={this.state.sessionCount}
        />
        <Controls />
        <Sound 
          sound={this.state.sound}
          toggleSound={this.toggleSound}
        />
      </div>
    )
  }
}
