import React, {Component } from 'react';
import './Sound.css';



export default class Controls extends Component {
    render() {
        let soundButton;
        if(this.props.toggleSound)  {
            soundButton = <button id="sound" onClick={this.toggleSound}></button>
        } else {
            soundButton = <button id="no_sound" onClick={this.toggleSound}></button>
        }
        return (

            <div className="sound_div">
                {soundButton}
            </div>
        )
    }
}
