import React, {Component } from 'react';
import './App.css';
import Setter from './components/Setter/Setter';
import Timer from './components/Timer/Timer';
import Controls from './components/Controls/Controls';
import Sound from './components/Sound/Sound';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        breakCount: 5,
        sessionCount: 25,
        sound: true, //sound is turned on or off
        cycle: 'Session',//are we in a break or session
        running: false, //if timer is running
        paused: true,//if paused is clicked
        currentTime: '25:00'
    }

    this.startTime = 'potato'; //what time was stopwatch started
    this.updatedTime= null; //time right now
    this.tInterval = 0; //How often we will check the difference between now and our start time
    this.savedTime = 0; //if user presses pause keep track of how long
    this.difference = 0; //the difference in current time and start time + saved time
}

    //Start the stopwatch
    startTimer = () => {
      if (!this.state.running) { //if the stopwatch is not already running
        this.tomato = new Date().getTime();
        //We are going to check the time every second
        let interval = setInterval(this.getShowTime, 1000);
        this.setState({
          startTime: newTime,
          running: true,
          tInterval: interval
        })
      }
    }
    //This is the timer function
    getShowTime = () => {
      let update = new Date().getTime();
      //if we hit the pause button, the elapsed time needs to be corrected and we must add the time spent waiting
      if (this.state.savedTime) {
        var diff = (this.state.updatedTime - this.state.startTime) + this.state.savedTime;
        this.setState({
          updatedTime: update,
          difference: diff
        })
      } else {
        var diff2 = this.state.updatedTime - this.state.startTime;
        this.setState({
          updatedTime: update,
          difference: diff2
        })
      }

      var minutes = Math.floor((this.state.difference % (1000 * 60 * 60)) / (1000 * 60));
      var seconds = Math.floor((this.state.difference % (1000 * 60)) / 1000);
      minutes = (minutes < 10) ? "0" + minutes : minutes;
      seconds = (seconds < 10) ? "0" + seconds : seconds;
    }
    //switch mode between session and break
    switchMode = () => {
      if (this.state.cycle === 'Session') {
        this.setState({
          cycle: 'Break'
        })
      }
      else {
        this.setState({
          cycle: 'Session'
        })
      }
    }

    //allows user to turn sound on or off
    toggleSound = () => {
      this.setState({
        sound: !this.state.sound
      })
    }

    //I think this will play the sound
    // setSound = (sound) => {
    //   this.setState({
    //     sound: sound
    //   })
    // };

    bIncrement = ()  => {
        if (this.state.breakCount < 60) {
            this.setState({
                breakCount: this.state.breakCount + 1,
              });
        }

      };

    bDecrement = () => {
        if (this.state.breakCount > 0) {
            this.setState({
                breakCount: this.state.breakCount - 1
            });
        }
      };

    //allows user to increment the session length
    sIncrement = () => {
        if (this.state.sessionCount < 60) {
            this.setState({
                sessionCount: this.state.sessionCount + 1,
                currentTime: `${this.state.sessionCount+1} : 00`
            });
            console.log(this.startTime)
        } 
    };

    //allows user to decrement the session length
    sDecrement = () => {
        if (this.state.sessionCount > 0) {
            this.setState({
                sessionCount: this.state.sessionCount - 1,
                currentTime: `${this.state.sessionCount-1} : 00`
            });
        }
    };

    // countDown = () => {
    //   // if (this.state.sessionCount !== 0) {
    //   //   startTime = new Date().getTime();
    //   //   tInterval = setInterval(getShowTime, 1);
    //   // }
    // }
  render() {
    return (
      <div className="container_div">
        <h1>Pomodoro Clock</h1>
        <Setter 
          breakCount={this.state.breakCount}
          sessionCount={this.state.sessionCount}
          bIncrement={this.bIncrement}
          bDecrement={this.bDecrement}
          sIncrement={this.sIncrement}
          sDecrement={this.sDecrement}
        />
        <Timer 
          breakCount={this.state.breakCount}
          sessionCount={this.state.sessionCount}
          currentTime={this.state.currentTime}
          cycle={this.state.cycle}
        />
        <Controls />
        <Sound 
          sound={this.state.sound}
          toggleSound={this.toggleSound}
        />
      </div>
    )
  }
}
